def card_comment_gen(card_id):
    str_format = "//#"+'%56s' % ("#"+cards[card_id]['type']+"#-#"+cards[card_id]['cardClass'])
    str_format = str_format.replace(" ", "-")
    str_format = str_format.replace("#", " ")
    str_format = str_format + comm + "[" + cards[card_id]['id'] + "] " + cards[card_id]['name'] + " - COST: "
    if("cost" in cards[card_id].keys()):
        str_format = str_format + str(cards[card_id]['cost'])
    else:
        str_format = str_format + str(0)
    if(cards[card_id]['type'] == "MINION"):
        str_format = str_format + " [ATK: " + str(cards[card_id]['attack']) + "/HP: " + str(cards[card_id]['health']) + "]"
    str_format = str_format + comm + " - "
    if("race" in cards[card_id].keys()):
        str_format = str_format + "Race: " + cards[card_id]['race']+", "
    if("faction" in cards[card_id].keys()):
        str_format = str_format + "Faction: " + cards[card_id]['faction']+", "
    str_format = str_format + "Set: " + cards[card_id]['set']
    if("rarity" in cards[card_id].keys()):
        str_format = str_format+", " + "Rarity: " + cards[card_id]['rarity'].capitalize()
    str_format = str_format + div
    if ("text" in cards[card_id].keys()):
        cards[card_id]['text'] = cards[card_id]['text'].replace("[x]","")
        cards[card_id]['text'] = cards[card_id]['text'].replace("$","")
        cards[card_id]['text'] = cards[card_id]['text'].replace("\n","\n//       ")
        str_format = str_format + comm + "Text: " + cards[card_id]['text'] + div

    # if card has GameTag, PlayReq, or RefTag, add it.

    # GameTag
    if("mechanics" in cards[card_id].keys() or "elite" in cards[card_id].keys()):
        str_format = str_format + comm + "GameTag:"
        if ("elite" in cards[card_id].keys()):
            str_format = str_format + comm + " - ELITE = 1"
        if ("mechanics" in cards[card_id].keys()):
            for tag in cards[card_id]['mechanics']:
                str_format =  str_format + comm + " - " + tag + " = 1"
            str_format = str_format + div
    # How About PlayReq?

    # RefTag
    if("referencedTags" in cards[card_id].keys()):
        str_format = str_format + comm + "RefTag:"
        if ("referencedTags" in cards[card_id].keys()):
            for tag in cards[card_id]['referencedTags']:
                str_format = str_format + comm + " - "  + tag + " = 1"
            str_format = str_format + div
    return str_format

